read -p "Enter version folder name: " version_folder
diff -y <(cat <(find zoneinfo -type f) <(find zoneinfo -type l) | sed 's/^zoneinfo\///;/^right/d;/^posix\//d;/^iso3166.tab$/d;/^zone.tab$/d;/^zone1970.tab$/d;/^Etc/d;/^leap-seconds.list$/d;/^localtime$/d;/CET/d;/CST6CDT/d;/EET/d;/EST/d;/EST5EDT/d;/HST/d;/MET/d;/MST/d;/MST7MDT/d;/PST8PDT/d;/WET/d;/SystemV/d' | sort | uniq) <(grep -oP '(?<=\()[^\)]+(?=\))' "${version_folder}_timezone_id_list.txt" | awk -F , '{for(i=1;i<=NF;i++) { gsub(/ /, "", $i); print $i}}' | sort | uniq) | grep '<' > "$version_folder"_comparison_result_only_zoneinfo.txt
diff -y <(cat <(find zoneinfo -type f) <(find zoneinfo -type l) | sed 's/^zoneinfo\///;/^right/d;/^posix\//d;/^iso3166.tab$/d;/^zone.tab$/d;/^zone1970.tab$/d;/^Etc/d;/^leap-seconds.list$/d;/^localtime$/d;/CET/d;/CST6CDT/d;/EET/d;/EST/d;/EST5EDT/d;/HST/d;/MET/d;/MST/d;/MST7MDT/d;/PST8PDT/d;/WET/d;/SystemV/d' | sort | uniq) <(grep -oP '(?<=\()[^\)]+(?=\))' "${version_folder}_timezone_id_list.txt" | awk -F , '{for(i=1;i<=NF;i++) { gsub(/ /, "", $i); print $i}}' | sort | uniq) | grep '>' > "$version_folder"_comparison_result_only_v1.txt